type extract = ( input: string, newline?: string ) => CommentData

function parseComments( input: string ): CommentData

namespace parseComments {
	export function setNewline(sequence: string): void
	export type newline = string
}

export interface CommentData {
	input: string
	newlineChar: string
	lines: string[]
	lineCount: number
	charCount: number
	matches: Array<CommentDescriptor>
}

export interface CommentDescriptor {
	groups: {
		whole: string
		commentOnly: string
		lines: {
			whole: string[]
			commentOnly: string[]
		}
	}
	column: {
		start: number
		end: number
	}
	line: {
		start: number
		end: number
	}
	inline: null|{
		pre: string|null
		post: string|null
	}
}

export default parseComments
